        long long r=0;
        vector<int> to_even;
        long long ans=(long long)b[n-1]; 
        for(int i=0; i<n;i++){if(b[i]==ans) {break;}to_even.push_back(b[i]);}
        long long cnt=1;
        for(int i=n;i<b.size();i++){ long long remain=(long long)b[i]+r;
            while(to_even.size()>0 ){ int target= to_even.back();
                cnt= n- to_even.size();
