class Solution {
public:
    long long maxRunTime(int n, vector<int>& B) {
        sort(B.begin(), B.end());
        reverse(B.begin(), B.end());
        long long int extra = 0, main = 0;
        for(int i = 0; i < n; i++){
            main += B[i];
        }
        for(int i = n; i < B.size(); i++) extra += B[i];

        long long int time = B[n-1];
        int i = n-1;
        while(i > 0 and extra > 0){
            if(extra >= (B[i-1] - B[i])*(n-i)){
                time += B[i-1] - B[i];
                extra -= (B[i-1] - B[i])*(n-i);
            }
            else{
                time += extra/(n-i);
                extra = 0;
            }
            i--;
        }
        time += extra/n;
        return time;
    }
};
