public class Solution {
    public long MaxRunTime(int n, int[] batteries) {
        long sum = 0;
        foreach (var b in batteries)
            sum += b;

        // binary search
        long left = 0;
        long right = sum / n;

        while (left < right)
        {
            long mid = left + (right - left + 1) / 2;

            if (CanFit(n, batteries, mid))
                left = mid;
            else
                right = mid - 1;
        }

        return left;
    }

	// Bigger battery can only provide the target power.
	// Smaller batter can just provide its full power.
    bool CanFit(int n, int[] batteries, long target)
    {
        long sum = 0;
        foreach (var b in batteries)
        {
            sum += Math.Min(target, b);
        }

        return sum >= target * n;
    }
}
