// --------------- Intuition for binary search-----------------------------------
// Let us take a value of minutes - M
// Now we check if it is possible to run N computers, simultaneously for M minutes

//------------ logic for search space reduction------------------------------------
// If yes - we can consider this value of M as a possible ans and search on all values greater than M
// Note - even after getting a yes, we dont stop searching, as we want to find the Max value of M. 
// If no - we need to reduce the value M, so we check the condition for values < M

// -------------Deciding the range for binary search-----------------------
//For simplicity let's keep the minimum running minutes = 0
//In the case where number of computers is 1, the max time for which it can run is some of runtime of all batteries, so
//max run time value = sum(values of batteries run time)

//-------------- How to check this ?  "If it is possible to run N computers, simultaneously for M minutes" -------------
// total running minutes required = N comp * M ninutes = NM minutes
// total minutes batteries can run simulatanesouly if given max minutes as M = sum of batteries runtime values with a map cap of M minutes
// if total minutes batteries can run simulatanesouly if given max minutes as M  >= total running minutes required, we return yes, else false


class Solution {
public:

    bool canRun(vector<int>& batteries, int computers, long long M){

        long long requiredRunMins = computers*M;
        for(long long b: batteries)
            requiredRunMins-=min(M,b);
        return (requiredRunMins<=0);

    }

    long long maxRunTime(int n, vector<int>& batteries) {

        long long maxMins = 0;
        long long possibleAns=0;

        for(int b : batteries)
            maxMins+=b;

        long long lMins=0, rMins=maxMins;
        while(lMins<=rMins)
        {
            long long midMins = lMins+(rMins-lMins)/2;
            if(canRun(batteries, n, midMins))
            {
                possibleAns = midMins;
                lMins = midMins+1;
            }
            else
            {
                rMins= midMins-1;
            }
        }
        return possibleAns;

    }
};

