class Solution {
public:
    bool check(long long target, int n, vector<int>& batteries, long long sum)
    {
        if(target*n > sum)
        {
            return false;
        }

        long long possible_target_sum = 0;

        for(int i = 0; i<batteries.size(); i++)
        {
            possible_target_sum += min(batteries[i]*1LL, target);
        }

        if(possible_target_sum >= target*n)
        {
            return true;
        }
        else
        {
            return false;
        }
    }
    long long maxRunTime(int n, vector<int>& batteries) {
        long long ans = -1;
        long long sum = 0;
        long long mx = 0;
        int b = batteries.size();

        for(int i = 0; i<b; i++)
        {
            sum += batteries[i]*1LL;
            mx = max(mx, batteries[i]*1LL);
        }

        long long lo = 0;
        long long hi = mx*b;

        while(lo <= hi)
        {
            long long mid = lo + (hi - lo)/2;
            if(check(mid, n, batteries, sum))
            {
                ans = mid;
                lo = mid+1;
            }
            else
            {
                hi = mid-1;
            }
        }

        return ans;
    }
};
