class Solution {
    public long maxRunTime(int n, int[] batteries) {
        if(n>batteries.length) return 0;
        Arrays.sort(batteries);
        long sum = 0;
        for(int i=0;i<batteries.length;i++)
        {
            sum += batteries[i];
        }

        // this is the maximum time we can run the computer(average)
        long maxTime=sum/n+1;
        long minTime = batteries[batteries.length-n];
        long ans=0;

        //now we will searching for the max time we can run 
        while(minTime <maxTime)
        {
            //we search for the max time
            long mid = minTime +(maxTime-minTime)/2;
            //this is iterator to move from right to left
            int k = batteries.length-1; int runnable=0;
            while(k>=0 && batteries[k]>=mid)
            {
                k--;
                runnable++;
            }
            //This is the remaining numbers of computers
            long remComp = n-runnable; 
            //This will count remaining charge and help check if remaining computers can run for (mid) time
            long temp =0;
            while(k>=0)
            {
                temp += batteries[k];
                k--;
            }
            //Check if it is possible to run the remaining computers
            if(remComp*mid<=temp)
            {
                ans = Math.max(ans,mid);
                minTime = mid+1;
            }
            else{
                maxTime = mid;
            }

        }
        return ans;

    }
}
