class Solution {
public:
    bool possible(int n, vector<int> &b, long long hrs){
        long long int sum = 0;
        for(int i=0; i<b.size(); i++){
            sum += b[i];
            if(sum >= hrs)
                sum -= hrs, n--;
            if(n==0) 
                return true;
        }
        return false;
    }

    long long maxRunTime(int n, vector<int> &b) {
        long long int start=1, end=0,mid=0;
        for(int i=0; i<b.size(); i++) 
            end += b[i];
        end /= n;

        sort(b.begin(), b.end());
        while(start < end){
             mid = (end+start+1)/2;
            if(possible(n, b, mid))
                start = mid;
            else
                end = mid-1;
        }
        return start;
    }
};
