        ll low=1,high=LONG_MAX;
        ll ans=0;
        sort(batteries.begin(),batteries.end(),greater<int>());
        ll batteriesLeft=0;
        //excess batteries after n batteries
        for(int i=n;i<batteries.size();++i){
            batteriesLeft+=batteries[i];
