typedef vector<int> vi;
typedef vector<vi> vvi;

class Solution {
public:
    long long maxRunTime(int n, vector<int>& b) {
        priority_queue<int> pq(b.begin(), b.end());
        long long runtime = 0;
        while(pq.size() >= n) {
            vi bat;
            int bats = n;
            int minVal = INT_MAX;
            while(bats--) {
                int val = pq.top();
                pq.pop();
                minVal = min(minVal, val);
                bat.push_back(val);
            }
             if(minVal > 1) {
                minVal--;
                runtime += minVal;
                for(int val: bat) {
                    pq.push(val-minVal);
                }
            } else {
                runtime++;
                for(int val: bat) {
                    if(val != 1)
                        pq.push(val-1);
                }
            }
        }
        return runtime;
    }
};
