```swift
class Solution {
    func threeSumClosest(_ nums: [Int], _ target: Int) -> Int {

        let sorted = nums.sorted()
        let length = sorted.count

        var diff: Int = .max
        var result = 0

        for i in 0..<length - 2 {
            var n = i + 1, q = length - 1
            while n < q {
                let sum = sorted[i] + sorted[n] + sorted[q]

                sum > target ? (q -= 1) : (n += 1)

                let value =  abs(sum - target)

                if value < diff {
                    diff = value
                    result = sum
                }
            }
        }
        return result
    }
}
```
