class Solution {
public:
    int threeSumClosest(vector<int>& nums, int target) {
        sort(nums.begin(),nums.end());
        int first = 0;
        int second = 1;
        int third = nums.size()-1;

        int sum = nums[0]+nums[1]+nums[2];

        for(; first<nums.size()-2 ; first++){
            second = first + 1;
            third = nums.size()-1;

            while(second<third){
                int curr_sum = nums[first] + nums[second] + nums[third];

                if(abs(target-curr_sum)< abs(target-sum)){
                    sum = curr_sum;
                }
                if(curr_sum > target){
                    third--;
                }
                else if(curr_sum < target){
                    second++;
                }
                else {
                    return curr_sum;
                }
            }
        }
        return sum;
    }
};
