Store all possible sum and then find the answer's index using upperbound.
Time Complexity O(n^2)
where n = size of the nums vector.

class Solution {
public:
int threeSumClosest(vector<int>& nums, int target) {

    vector<int>store;
    sort(nums.begin(),nums.end());		
    for(int i=0;i<nums.size();i++){
        int first = nums[i];
        int low = i+1;
        int high = nums.size()-1;
        while(low<high){
            int sum = nums[low]+nums[high]+first;
            store.push_back(sum);
            if(sum<target) low++;
            else high--;
        }
    }
    sort(store.begin(),store.end());
    int ind = upper_bound(store.begin(),store.end(),target)-store.begin();

    if(ind==store.size())  return store[ind-1];
    if(ind==0) return store[0];
    int dif1 = abs(store[ind]-target);
    int dif2 = abs(target-store[ind-1]);
    if(min(dif1,dif2)==dif1) return store[ind];
    return store[ind-1];
}
};

