                sum -=nums[j];
                j++;
                sum+=nums[j];
            }
            else if(sum > target){
                   sum -=nums[k];
                k--;
                sum+=nums[k];
            }
             else
             {
                 break;
             }
        }
    }
    return closesum;

}


