	public Node connect3(Node root) {
		if(root == null)
			return root;
		Queue<Node> q = new LinkedList<>();
		q.offer(root);

		while(!q.isEmpty()) {
			int size = q.size();
			Node prev = q.poll();
			if(prev.left != null) {
				q.offer(prev.left);
				q.offer(prev.right);
			}
			for(int i=1; i<size; i++) {
				Node curr = q.poll();
				prev.next = curr;
				if(curr.left != null) {
					q.offer(curr.left);
					q.offer(curr.right);
				}
				prev = curr;
			}
		}
		return root;
