```class Solution {
    private void helper(Node root)
    {
        if(root == null)
            return;
        //since it is a leaf node it wont have any child 
        if(root.left == null && root.right == null)
            return;
        //stand on parent and connect left child to right child
        root.left.next = root.right;

        //after connecting left child to right , connect parent's right child via the next link we created above to left child of adjacent subtree.
        if(root.next != null)
            root.right.next = root.next.left;

        helper(root.left);
        helper(root.right);
    }
    public Node connect(Node root) {
        helper(root);
        return root;
    }
}

