class Solution {
public:
    Node* connect(Node* root) {
        if(root == NULL) return root;
        queue<Node*> q;
        q.push(root);
        while(!q.empty()){
            int n=q.size();
            int j=0;
            for(int i=0;i<n;i++){
                Node* node = q.front();
                q.pop();
                j++;
                if(j<n){
                    Node* x = q.front();
                    node->next = x;
                }else{
                    node->next = NULL;
                }
                if(node->left) q.push(node->left);
                if(node->right) q.push(node->right);

            }
        }
        return root;
    }
};
