func connect(root *Node) *Node {
    if root == nil {
        return nil
    }

    q := []*Node{ root }

    for len(q) > 0 {
        var pre *Node

        for _, n := range q {
            q = q[1:]

            if pre != nil {
                pre.Next = n
            }

            pre = n

            if n.Left != nil {
                q = append(q, n.Left)
            }

            if n.Right != nil {
                q = append(q, n.Right)
            }
        }
    }

