class Solution {
    public Node connect(Node root) {
        populate(root);
        return root;
    }

    public void populate(Node root){

        if(root==null)
            return;
        // fill left's next
        if(root.left != null)
            root.left.next = root.right;

        // fill right's next based on the next of current node
        // this will work simply cuz the root's next
        // will always be filled before coming at this line 
        // Or it will be null in case if the root is the most
        // right on it's level
        if(root.right!=null && root.next !=null)
            root.right.next = root.next.left;

        // Do the same for left and right branches
        populate(root.left);
        populate(root.right);
    }
}
