class Solution {
    public Node connect(Node root) {
        if(root == null) return root;
        Queue<Node> queue = new LinkedList<>();
        queue.add(root);

        while(!queue.isEmpty()){
            int size = queue.size();

            while(size--!=0){
               Node node = queue.poll();

               if(size == 0){
                   node.next = null;
               }else{
                   node.next = queue.peek();
               }

                if(node.left != null)
                queue.offer(node.left);

                 if(node.right != null)
                queue.offer(node.right);

            }
        }

        return root;

    }
}
