class Solution {
public:
    void helper(vector<vector<int>> &res, vector<int> &ds, int count, int sum, int start)
    {
        // if we reached to our answer
        if(sum==0 && count==0)
        {
            res.push_back(ds);
            return;
        }

        // if any one of them (k,n) gets 0 then return
        if(sum==0 or count==0)
            return;

        // if value cross 9 then also return
        if(start>9)
            return;

        // if currentvalue lies in boundary then take it
        if(sum>=start && count>0)
        {
            ds.push_back(start);                               // taking the current value
            helper(res, ds, count-1, sum-start, start+1);      // now calling the same function for next
            ds.pop_back();                                     // backtracking
        }

        // else skipping the current value if it is not is the bound
        helper(res, ds, count, sum, start+1);
    }

    vector<vector<int>> combinationSum3(int k, int n) 
    {
        // craeting the 2d bector for storing the list of list (final answer)
        vector<vector<int>> res;

        // creating the vector that will store the combinations one by one 
        vector<int> ds;

        // we are going to use recursion here because we need to check all possible combinations for n by using int between 1 to 9
        helper(res, ds, k, n, 1);

        return res;
    }
};
