/*

    Time Complexity : O(k*nCk), here nCk means the binomial coefficient of picking k elements out of n elements.
    where nCk = C(n,k) = n!/(n−k)!\xD7k!.

    Space Complexity : O(nCk), In worst case as stated above the nCk here refers to the binomial coefficient.

    Solved using Array + Backtracking.

*/

class Solution {
private:
    void combinationSum3(int n, int k, vector<vector<int>> &output, vector<int> &temp, int start, int sum){
        if(temp.size() == k){
            if(n == sum){
                output.push_back(temp);
            }
            return;
        }
        for(int i=start; i<=9; i++){
            temp.push_back(i); 
            combinationSum3(n, k, output, temp, i+1, sum+i);
            temp.pop_back(); 
        }
    }     
public:
    vector<vector<int>> combinationSum3(int k, int n) {
        vector<vector<int>> output;
        vector<int> temp;
        combinationSum3(n, k, output, temp, 1, 0);
        return output;
    }
};

