class Solution {
public:
    void combinationSumHelper( int start, int k, int n, vector<int> &ds, vector<vector<int>> &ans ){

        if( k<= 0 ){
            // We have got our combination 
            if( n == 0 ) ans.push_back( ds );
            return ;
        }

        for( int i=start ; i<=9 ; i++ ){

			// Push the element 
            ds.push_back( i );
			// Find out the combinations possible with this number 
            combinationSumHelper( i+1, k-1, n-i, ds, ans );
			// Backtracking 
            ds.pop_back();
        }
    }

    vector<vector<int>> combinationSum3(int k, int n) {

        vector<vector<int>> ans ;
        vector<int> ds ;

        combinationSumHelper( 1, k , n, ds, ans );
        return ans ;
    }
};
