### Solution
```js
/**
 * @param {number} k
 * @param {number} n
 * @return {number[][]}
 */
const backtrack = (remain, k,arr, next_start, results) => {
  if (remain === 0 && arr.length === k) {
        results.push(new Array(...arr));
        return;
  } else if (remain < 0 || arr.length === k) {
        return;
  }

  // Iterate through the reduced list of candidates.
  for (let i = next_start; i < 9; ++i) {
        arr.push(i + 1);
        backtrack(remain - i - 1, k, arr, i + 1, results);
        arr.pop();
  }
}

var combinationSum3 = function(k, n) {
    let results = [];

    if (k > n) {
        return [];
    }

    backtrack(n, k, [], 0, results);
    return results;
};
```


