class Solution {
public:
    // support variables
    vector<vector<int>> res;
    vector<int> tmp;
    int k, n;
    // dfs generator
    void dfs(int start = 0) {
        // base case, only the last element potentially missing
        if (k == 1) {
            // allowing only for single digit insertions
            if (n < 10) {
                tmp.push_back(n);
                res.push_back(tmp);
                tmp.pop_back();
            }
            return;
        }
        // updating k
        k--;
        for (int i = start + 1, lmt = (k + 2) * (k + 1) / 2 + start * (k + 1); n >= lmt; i++) {
            // updating n and tmp
            n -= i;
            tmp.push_back(i);
            dfs(i);
            // backtracking n and tmp
            n += i;
            tmp.pop_back();
            lmt += k + 1;
        }
        // backtracking k
        k++;
    }
    vector<vector<int>> combinationSum3(int _k, int _n) {
        k = _k;
        n = _n;
        // we proceed only if k < 10 and n is achievable with just 9 digits
        if (k < 10 && n < 46) dfs();
        return res;
    }
};
```

