func backtrack(res *[][]int, k, n, sum int, curSequence []int, used map[int]struct{}) {
    if sum == n && len(curSequence) == k{
        cs := make([]int, len(curSequence))
        copy(cs, curSequence)
        *res = append(*res, cs)
        return
    }

    for i := 1; i <= 9; i++ {
        if _, ok := used[i]; ok {
            continue
        }

        switch {
        case len(curSequence) > k:
            continue
        case len(curSequence) > 0 && i <= curSequence[len(curSequence)-1]:
            continue
        case sum > n:
            continue
        }

        used[i] = struct{}{}
        curSequence = append(curSequence, i)
        backtrack(res, k, n, sum+i, curSequence, used)
        delete(used, i)
        curSequence = curSequence[:len(curSequence)-1]
    }
}

func combinationSum3(k int, n int) [][]int {
    var res [][]int
    used := make(map[int]struct{})
    backtrack(&res, k, n, 0/*sum*/, []int{} /*curSequence*/, used)
    return res
}
