class Solution {
    public List<List<Integer>> combinationSum3(int k, int n) {
        List<List<Integer>> nm=new ArrayList<List<Integer>>();
        if(n<k*(k+1)/2)
            return nm;
        task(1,k,n,new LinkedList<>(),nm);
            return nm;
    }
    static void task(int s, int k, int n, LinkedList<Integer> kk, List<List<Integer>> nm)
    {
        if(k<0||n<0)
            return;
        if(n==0&&k==0)
        {
            nm.add(new ArrayList<>(kk));
            return;
        }
        for(int i=s;i<=9;i++)
        {
            kk.add(i);
            task(i+1,k-1,n-i,kk,nm);
