vector<vector<int>> combinationSum3(int k, int n) 
{
	vector<vector<vector<int>>> dp(n + 1);
	dp[0].push_back(vector<int>());

	for (int i = 1; i <= 9; ++i)
		for (int j = i; j <= n; ++j)
			for (auto arr : dp[j - i])
				if (arr.size() < k && (j < n || arr.size() == k - 1) && (arr.empty() || arr.back() != i))
					dp[j].push_back(arr),
					dp[j].back().push_back(i);

	return dp[n];
}
