        arr[5]=6;
        arr[6]=7;
        arr[7]=8;
        arr[8]=9;
     List<List<Integer>> ans=new ArrayList<>();
        rec(arr,k,0,n,0,new ArrayList<Integer>(),ans);
        return ans;
    }

    public void rec(int[] cd, int k,int idx, int tgt,int depth,List<Integer> temp,List<List<Integer>> ans){

        if(tgt<0 || depth>k)
            return;

        if(tgt==0){
            if(depth==k)
            ans.add(new ArrayList<Integer>(temp));
            return;
        }

        for(int i=idx;i<cd.length;i++){

             temp.add(cd[i]);
             rec(cd,k,i+1,tgt-cd[i],depth+1,temp,ans);
             temp.remove(temp.size()-1);

        }

    }
}

