let combinationSum3 = (K, T, paths = []) => {
    let go = (start = 1, t = 0, path = []) => {
        if (path.length == K) {
            if (t == T)
                paths.push([...path]);
            return;
        }
        for (let x = start; x <= 9; ++x)
            go(x + 1, t + x, path.concat(x));
    };
    go();
    return paths;
};
```

