# Brute Force	

     bool check(string &x,string &y,int i){
	        if(i==y.size()) return true;
            return x[i]==y[i]? check(x,y,i+1) : false;
	 }

     int prefixCount(vector<string>& words, string &pref){
        int cnt=0;
        for(auto i:words){
            if(i.size()<pref.size()) continue;
            cnt+= check(i,pref,0)? 1 : 0 ;
        }
        return cnt;
    }
