class Solution {
    public int prefixCount(String[] words, String pref) {

        int ans =0;
        int len = words.length;
        int plen = pref.length();

        for(int i=0;i<len;i++){

            if(words[i].length() < plen)continue;

            if(pref.equals(words[i].substring(0,plen))==true)ans++;
        }
        return ans;
    }
}
