        for (int i = 0; i < t.length(); i++) {
            str2[t.charAt(i) - 'a'] += 1; 
        }

        boolean isAnagram = true;
        for (int i = 0; i < 26; i++) {
            if (str1[i] != str2[i]) isAnagram = false;
        }

        if (isAnagram) return 0;
        int count = 0;
        for (int i = 0; i < 26; ) {
            if (str1[i] == str2[i]) {
                i++;
                continue;
            }

            if (str1[i] > str2[i]) {
                str2[i] += 1;
                count++;
            } else if (str1[i] < str2[i]) {
                str1[i] += 1;
                count++;
            }

             if (str1[i] == str2[i]) {
                i++;
                continue;
            }
        }

        return count;
    }
}
