class Solution {
public:
    int minSteps(string s, string t) {
        sort(s.begin(),s.end());
        sort(t.begin(),t.end());

        // If words are anagram, then sorting both of them would make the strings equal i.e s == t.
        if (s == t)
            return 0;

        // If the are not equal then we take two pointer approach:

        int i = 0,j = 0,mini = 0;
        int n = s.length(), m = t.length();
        while (i < n && j < m){
            int check1 = s[i]-'a';
            int check2 = t[j]-'a';

            // If 'i'th and 'j'th letter in both the words are same we won't require any steps,
            // so we just continue, incrementing 'i'th and 'j'th pointers;

            if (check1 == check2){
                i++;
                j++;
            }

            // If 'i'th letter of string s is less than 'j'th letter of string t,
            // then we need to add 'i'th letter of string s to string t in order to make them anagram.
            else if (check1 < check2){
                i++;
                mini++;
            }

            // If 'i'th letter of string s is greater than 'j'th letter of string t,
            // then we need to add 'j'th letter of string t to string s in order to make them anagram.
            else{
                j++;
                mini++;
            }      
        }

