long long minimumTime(vector<int>& time, int totalTrips) {
    long long l = 0, r = LLONG_MAX / time.size();
    while (l < r) {
        long long m = (l + r) / 2, trips = 0;
        for (int t : time)
            trips += m / t;
        if (trips < totalTrips)
            l = m + 1;
        else
            r = m;
    }
    return l;
}
