class Solution {
public:
    long long solve(vector<int> reqTime, int totalTrips) {
        long long start = 0;
        long long end = 1e14;
        long long ans = 0;

        while(start <= end) {

            long long mid = start +(end-start) / 2;
            long long trips = 0;

            // Considering mid as required time. If we get desired count of totalTrips by using time == mid.
            // it will store that ans, else if(required trips < totalTrips), start = mid + 1.
            for(auto reqTimee : reqTime) {
                trips += mid / reqTimee;
            }

            if(trips >= totalTrips) {
                ans = mid;
                end = mid-1;
            }
            else {
                start = mid+1;
            }
        }
        return ans;
    }

    long long minimumTime(vector<int>& time, int totalTrips) {
        return solve(time, totalTrips);
    }
};
```

