public:
    int mo = 1e9+7;
    bool f(long long x,vector<int>&time,int totalTrips){
        long long int ans = 0;
        for(int i = 0;i < time.size();i++){
            ans += (long long)(x / time[i]);
        }
        if(ans >= totalTrips)return true;
        return false;
    }

    long long minimumTime(vector<int>& time, int totalTrips) {
        sort(time.begin(),time.end());
        int n = time.size();
        long long  minTime = 1;
        long long  maxTime = ((long long)(time[n-1]) * (long long)(totalTrips));
        while(minTime < maxTime){
            long long  mid = (minTime + maxTime) / 2;
            // cout << minTime << " " << maxTime << endl;
            if(f(mid,time,totalTrips)){
                maxTime = mid;
            }
            else{
                minTime = mid + 1;
            }
        }
        return minTime;
    }
};
```

