    long long minimumTime(vector<int>& time, int totalTrips) {
        long long mint = *min_element(time.begin(), time.end());
        long long mid, l = mint, r = totalTrips * mint;
        while(l <= r) {
            mid = l + (r - l) / 2;
            if(canMakeTrips(time, totalTrips, mid))
                r = mid - 1;
            else
                l = mid + 1;
        }
        return l;
    }
};
