class Solution {
    func minimumTime(_ time: [Int], _ totalTrips: Int) -> Int {
        var left = time.min()!
        var right = time.max()! * totalTrips

        func timeEnough(_ int: Int) -> Bool {
            var tripsCount = 0

            for t in time {
                tripsCount += (int / t)
            }

            return tripsCount >= totalTrips
        }

        while left < right {
            let mid = (left + right) / 2

            if timeEnough(mid) {
                right = mid
            } else {
                left = mid + 1
            }
        }

        return left
    }
}
