class Solution {
public:
    long long minimumTime(vector<int>& t, int ttl) {
        if(t.size() == 1){
            return (long long)t[0] * ttl; //result will total time to complete totaltrip by bus 0
        }
        sort(t.begin(),t.end());
        long long s = 0;
        int n = t.size();
        long long e = (long long)t[n-1]*ttl;  // max time taken will be the bus with maximum time 
        long long res  = e;
        while(s<=e){
            long long mid = s+(e-s)/2;
            long long ans = 0;
            for(int i  = 0;i<t.size();i++){
                ans += (long long)(mid/t[i]); //checking each bus that how much trips does i^th bus take in mid time
            }
            if(ans>=ttl){
                res = mid;
                e = mid-1;
            }else{
                s = mid+1;
            }
        }        
        return res;
    }
};


