    long long minimumTime(vector<int>& time, int totalTrips) {
        long long h=INT_MAX, l=0; // h = end(high) time limit and l= start(low) time limit
        for(int i=0;i<time.size();i++)
        {
            if(time[i]<h){
                h=time[i];  // give the time of the fastest bus or the min. time
            }
        }
        h=totalTrips*h; // time taken by the fastest bus to complete all the trips individually
        while(l<=h){
            // cout<<l<<" "<<h<<endl;
            long long midTime=l+(h-l)/2;  //min time that may be possbile
            long long curTrips=0;  // no. of total trips that could be done by all the buses cumulative within the midTime 
            for(int j=0;j<time.size();j++)
            {
                curTrips+=(midTime/time[j]);  
            }
            if(curTrips>=totalTrips){  // if total trip within midTime > totaltrips => midTime is more than the actual time so decreasing the end-time limit
                h=midTime-1;
            }else {
                l=midTime+1; //else increasing the start-time limit
            }
        }
        return l;  
    }
};
