const removeNthFromEnd = (head, n) => {
  const llMap = [];

  while (head) {
    llMap.push(head);
    head = head.next;
  }

  const prevNode = llMap[llMap.length - n - 1];

  if (!prevNode) return llMap[0].next;

  prevNode.next = prevNode.next.next;

  return llMap[0];
}
```

