class Solution {
public:
    ListNode* removeNthFromEnd(ListNode* head, int n) {

        int count = 0;
        ListNode *curr = head;

        while(curr){
            count++;
            curr = curr->next;
        }

        if(count == n){
            head = head->next;
            return head;
        }


        int k = count-n;

        ListNode *temp = head;
        count = 1;

        while(count!=k){

            temp = temp->next;
            count++;
        }

        ListNode *var = temp->next;
        temp->next = temp->next->next;
        delete(var);

        return head;
    }
};

