// to calculate the number of elements in linked list
int length(ListNode* head){
	int len=0;
	while(head!=NULL)
		head = head->next, len++;
	return len;
}

ListNode* removeNthFromEnd(ListNode* head, int n) {
	int len = length(head);
	// if n is same as length, it means we need to remove the first element
	if(n==len){
		head = head->next; return head;
	}

	int count=1;
	ListNode* temp = head;
	// running the while loop n number of times, 
	// and stopping just before the last nth node, i.e., prev node before last nth node.
	while(count!=len-n){
		temp= temp->next;
		count++;
	}

	// now its just simple delete node example
	// we point the next of the prev node to next of last nth node.
	if(temp!= NULL && temp->next!=NULL)
		temp->next= temp->next->next;
	return head;
}
