        //finding the lenght of the list
        while(ptr != null)
        {
            len++;
            ptr = ptr.next;
        }

        //required index
        int index = len - n;
        ptr = head;

        boolean start = false;

        while(true)
        {
            if(index == 0)
            {
                // if head element
                if(ptr == head)
                {
                    ptr2 = ptr2.next;
                    ptr.next = null;
                    head = ptr2;
                    break;
                }

                else
                {
                    ptr2.next = ptr.next;
                    ptr.next = null;
                    break;
                }
            }

            index--;

            //for letting the second pointer start late
            if(start)
                ptr2 = ptr2.next;

            start = true;

            ptr = ptr.next;
        }    

        return head;    
    }
}

