class Solution {
    // - Complexity:
    //   - time: O(k), where k is the rowIndex.
    //   - space: O(k), where k is the rowIndex.

    func getRow(_ rowIndex: Int) -> [Int] {
        var row = Array(repeating: 0, count: rowIndex + 1)
		row[0] = 1
		var index = 0

        while index <= rowIndex {
			for i in 0...index / 2 {
				let a = i > 0 ? row[i - 1] : 0
				let b = row[i]
				row[index - i] = a + b
			}

            for i in 0...index / 2 {
				row[i] = row[index - i]
			}

            index += 1
		}

		return row
    }

}
