            for (int j = 0; j <= i; j++) {
                if (j == 0 || j == i) currRow.add(1);
                else {
                    currRow.add(
                            prevRow.get(j - 1) + prevRow.get(j)
                    );
                }
            }

            ans.add(currRow);
            prevRow = currRow;
        }

        return ans.get(rowIndex);
    }
}

// TC: O(n ^ 2), SC; O(n ^ 2)
