class Solution {
    public List<Integer> getRow(int rowIndex) {        
        List<Integer> row = new ArrayList<>();
        row.add(1);

        if (rowIndex == 0) {
            return row;
        }

        row.add(1);
        if (rowIndex == 1) {
            return row;
