        while (currentIndex < rowIndex) {
            int size = row.size();
            int current = 0;
            while (current < size - 1) {
                row.add(row.get(0) + row.get(1));
                row.remove(0);
                current++;
            }
            row.add(1);
            currentIndex++;
        }

        return row;
    }
}
