class Solution {
    public List<Integer> getRow(int rowIndex) {
       List<List<Integer>> dp = new ArrayList<List<Integer>>();
        ArrayList<Integer> sp = new ArrayList<Integer>();
        sp.add(1);
        dp.add(sp);

