            int p1 = 0;
            int p2 = 1;
            while(p2 < use.size()){
                int sum = use.get(p1)+use.get(p2);
                sp3.add(sum);
                p1++;
                p2++;
            }
            sp3.add(1);
            dp.add(sp3);
        }
        return dp.get(rowIndex);
    }
}
